mapper.update:
update bl_user set
version=version+1

    ,username=#{username}


    ,password=#{password}


    ,name=#{name}


    ,sex=#{sex}


    ,header=#{header}


    ,nickname=#{nickname}


    ,user_email=#{userEmail}

where user_id=#{userId} and version=#{version}

mapper.delete:
update bl_user set
deleted=1
where user_id=#{id}

mapper.select:
select user_id, username, password, name, sex, header, nickname, user_email, created_time, update_time, version from bl_user where deleted=0
and username like CONCAT('%',#{params.username},'%')
and name like CONCAT('%',#{params.name},'%')
and sex = #{params.sex}
and nickname like CONCAT('%',#{params.nickname},'%')
and user_name like CONCAT('%',#{params.userEmail},'%')
and created_time like CONCAT('%',#{params.createdTime},'%')
order by ${sortColumn} ${sortMethod}
limit #{index},#{pageSize}

select user_id,username,version from bl_user where user_id in
#{id}
and deleted = 0

select user_id, username, password, name, sex, header, nickname, user_email, deleted
from bl_user
where username=#{username}
